import React, { useState } from 'react';

const App = () => {

    // const state = useState();

    const [count, setCount] = useState(0);

    const incNum = () => {
        if(count == 0)
            setCount(count + 10);
        else if(count == 100)
            setCount(count - 10);
    };

    return (
        <>
            <h1>{count}</h1>
            <button onClick={incNum}>Click Me</button>
        </>
    );
};

export default App;
