import React from 'react';
import ReactDOM from 'react-dom';

let currHour = new Date(2002, 4, 1);
currHour = currHour.getHours();

let currText = "";

if(currHour >= 1 && currHour < 12){
  currText = "Good Morning";
}
else if(currHour >= 12 && currHour < 19){
  currText = "Good Afternoon";
}
else{
  currText = "Good Night";
}

ReactDOM.render(
  <>
    <h1>Hello Sir, {currText}</h1>
  </>,
  document.getElementById('root')
);
