import React, { useState } from 'react';


const App = () =>{
    const purple = "#8e44ad";

    const [name, setName] = useState("Click Me");

    const [bg, setBg] = useState(purple);

    const bgChange = () => {
        let newBg = "#34495e";
        setBg(newBg);
        setName("OuchðŸ˜±");
    };

    const bgBack = () => {
        setBg(purple);
        setName("AyyoðŸ˜ ");
    };

    return (
        <>
            <div style={{backgroundColor : bg}}>
                <button onClick={bgChange} onDoubleClick={bgBack} >{name}</button>
            </div>
        </>
    );
}

export default App;
