import React, { useState } from 'react';


const App = () =>{
    const purple = "#8e44ad";

    const [name, setName] = useState("Click Me");

    const [bg, setBg] = useState(purple);

    const bgChange = () => {
        let newBg = "#34495e";
        let letterTemp = "OuchðŸ˜±";
        setBg(newBg);
        setName(letterTemp);
    };

    return (
        <>
            <div style={{backgroundColor : bg}}>
                <button onClick={bgChange}>{name}</button>
            </div>
        </>
    );
}

export default App;
