import React, { useState } from 'react';

const bgChange = () => {
    const purple = "#8e44ad";
    const [bg, setBg] = useState(purple);
};

const App = () =>{
    return (
        <>
            <div style={{backgroundColor : bg}}>
                <button onClick={bgChange}>Click Me</button>
            </div>
        </>
    );
}

export default App;
